mapper.select:
select count(*) 
from ITEM_ASK 
where ITEM_ASK_ITEMID = #{ITEM_ID}

select *
from(select rownum rnum, a.*
 	 from (select * 
	   	   from ITEM_ASK
	   	   order by ITEM_ASK_DATE desc) a
 	 where rownum <= #{end}
 	)
where rnum >= #{start} and rnum <= #{end}
and ITEM_ASK_ITEMID = #{product.ITEM_ID}
