mapper.select:
     select item.*,  nvl(cnt,0) cnt
	  from item left outer join (select review.review_item_id, count(*) cnt
									from review
									group by review_item_id) c
	  on item.item_id = c.review_item_id
      from item
      where ITEM_CATEGORY = 'treat'

			and ITEM_NAME = #{sex}      


			and ITEM_NAME = #{weight}      


			and ITEM_NAME = #{species}

select *
from(select rownum rnum, a.*
 	 from (select * 
	   	   from item
	   	   where MEMBER_ID = #{member_id}
	   	   order by ITEM_REG_DATE desc) a
 	 where rownum <= #{end}
 	)
where rnum >= #{start} and rnum <= #{end}

mapper.update:
update item
set ITEM_NAME = #{ITEM_NAME}, ITEM_CATEGORY = #{ITEM_CATEGORY}, ITEM_PRICE = #{ITEM_PRICE}, ITEM_CONTENT = #{ITEM_CONTENT}, ITEM_IMAGE_ORIGINAL = #{ITEM_IMAGE_ORIGINAL}, ITEM_IMAGE_FILE = #{ITEM_IMAGE_FILE}, ITEM_SEX = #{ITEM_SEX}, ITEM_WEIGHT = #{ITEM_WEIGHT}, ITEM_SPECIES = #{ITEM_SPECIES}
where ITEM_ID = #{ITEM_ID}

mapper.select:
select *
from wish
where WISH_ITEM_ID = #{iTEM_ID}
and WISH_MEMBER_ID = #{member_id}
