mapper.select:
select *
from(select rownum rnum, a.*
 	 from (select * 
	   	   from item
	   	   where MEMBER_ID = #{member_id}
	   	   order by ITEM_REG_DATE desc) a
 	 where rownum <= #{end}
 	)
where rnum >= #{start} and rnum <= #{end}

mapper.update:
update item
set ITEM_NAME = #{ITEM_NAME}, ITEM_CATEGORY = #{ITEM_CATEGORY}, ITEM_PRICE = #{ITEM_PRICE}, ITEM_CONTENT = #{ITEM_CONTENT}, ITEM_IMAGE_ORIGINAL = #{ITEM_IMAGE_ORIGINAL}, ITEM_IMAGE_FILE = #{ITEM_IMAGE_FILE}, ITEM_SEX = #{ITEM_SEX}, ITEM_WEIGHT = #{ITEM_WEIGHT}, ITEM_SPECIES = #{ITEM_SPECIES}
where ITEM_ID = #{ITEM_ID}

mapper.select:
select count(*)
from item
where MEMBER_ID = #{member_id}

select *
from wish
where WISH_ITEM_ID = #{iTEM_ID}
and WISH_MEMBER_ID = #{member_id}

select count(*)
from wish
where WISH_MEMBER_ID = #{member_id}

select * 
	from ( select rownum rnum, b.*
	  from (select wish.*
			from wish 
			where wish_member_id = #{member_id}
			order by wish_rsv_date desc) b
	  where rownum <= #{end}
	)
where rnum >= #{start} and rnum <= #{end}

mapper.delete:
delete wish
where WISH_ITEM_ID = #{item_id}
and WISH_MEMBER_ID = #{user_id}
